={{ /* Get only the first name, make lowercase, remove spaces and special characters and convert to UTF-8 */

encodeURIComponent(
    $json.user_data.name.trim().split(" ").first()
        .toLowerCase()
        .replace(/\s+/g, '')
        .replace(/[^\p{L}\p{N}]/gu, '')
)

}}

={{ /* Get only the last name, make lowercase, remove spaces and special characters and convert to UTF-8 */

encodeURIComponent(
    $json.user_data.name.split(" ").last()
        .toLowerCase()
        .replace(/\s+/g, '')
        .replace(/[^\p{L}\p{N}]/gu, '')
)

}}

={{ /* Convert to Unix timestamp integer */

Math.floor(new Date($json.event_time).getTime() / 1000)

}}

