// Lấy output là chuỗi JSON từ AI Agent
const storyboardJsonString = $json.output;

// Lấy yêu cầu gốc của người dùng từ node trigger
// QUAN TRỌNG: Đảm bảo tên node trigger của anh là "When chat message received"
const originalRequest = $('Webhook').first().json.text;

// Tạo một đối tượng mới chứa cả hai thông tin
const combinedData = {
  storyboard: storyboardJsonString,
  originalRequest: originalRequest
};

// Trả về dữ liệu đã được gộp
return {
  originalRequest: originalRequest,
  storyboard: storyboardJsonString
};

// Lấy nội dung từ ChatInput và storyboard từ AI Agent
const originalText = String($('Webhook').first().json.chatInput || '');
const storyboard = $json.output || $json.storyboard || '';  // lấy đúng field trả ra từ AI Agent

// Hàm trích xuất nội dung sau nhãn
const extract = (label) => {
  const regex = new RegExp(`${label}:\\s*(.*)`);
  const lines = originalText.split('\n');
  for (const line of lines) {
    const match = line.match(regex);
    if (match) return match[1].trim();
  }
  return null;
};

// Trích xuất các trường cần thiết
const videoType = extract("Thể loại video");
const tone = extract("Tone & mood");
const durationRaw = extract("Thời lượng");
const duration = durationRaw ? parseInt(durationRaw) : 30;

// Gán toàn bộ storyboard là summary
const summary = storyboard;

// Trả về kết quả
return [{
  videoType,
  tone,
  duration,
  summary
}];

// Bước 1: Lấy output từ node AI bằng đúng tên biến là "output"
const rawOutput = $json.output || '';

// Kiểm tra xem có lấy được dữ liệu không
if (!rawOutput) {
    throw new Error('Không nhận được dữ liệu từ node AI. Biến $json.output đang rỗng.');
}

let jsonString;

// Ưu tiên tìm khối markdown ```json ... ``` trước
const markdownMatch = rawOutput.match(/```json\s*([\s\S]*?)\s*```/);

if (markdownMatch && markdownMatch[1]) {
    jsonString = markdownMatch[1].trim();
} else {
    // Nếu không, tìm chuỗi JSON/JSON Array trong output thô
    const startIndex = rawOutput.search(/[[{]/);
    if (startIndex === -1) {
        throw new Error('Đã nhận được dữ liệu, nhưng không tìm thấy ký tự [ hoặc {.');
    }

    const lastBracket = rawOutput.lastIndexOf(']');
    const lastBrace = rawOutput.lastIndexOf('}');
    const endIndex = Math.max(lastBracket, lastBrace);
    if (endIndex === -1) {
        throw new Error('Đã nhận được dữ liệu, nhưng không tìm thấy ký tự ] hoặc }.');
    }

    jsonString = rawOutput.substring(startIndex, endIndex + 1).trim();
}

// Parse chuỗi JSON đã được làm sạch
let parsed;
try {
    parsed = JSON.parse(jsonString);
} catch (e) {
    throw new Error('Lỗi parse JSON: ' + e.message + '. Chuỗi đã cố gắng parse: ' + jsonString);
}

// Nếu AI trả về một mảng, return trực tiếp. Nếu trả về một object, bọc nó trong mảng.
if (Array.isArray(parsed)) {
    return parsed;
} else {
    return [parsed];
}

