  if (markdownMatch && markdownMatch[1]) {
    jsonString = markdownMatch[1].trim();
  } else {
    const startIndex = rawOutput.search(/[{\[]/);
    if (startIndex !== -1) {
      jsonString = rawOutput.substring(startIndex).trim();
    } else {
      throw new Error("Không tìm thấy JSON trong output.");
    }
  }
}

try {
  const parsed = JSON.parse(jsonString);

  // ✅ Làm sạch tất cả chuỗi: bỏ `\n`, gom khoảng trắng
  for (const key in parsed) {
    if (typeof parsed[key] === 'string') {
      parsed[key] = parsed[key].replace(/\\n/g, ' ').replace(/\s+/g, ' ').trim();
    }
  }

  return [parsed];
} catch (e) {
  throw new Error("Lỗi parse JSON: " + e.message + ". Chuỗi đã cố gắng parse:\n" + jsonString);
}

