// Lấy toàn bộ dữ liệu từ các item trước
const data = items.map(item => item.json);

// Nếu data là mảng → dùng luôn
// Nếu không phải mảng → thử lấy ra key đầu tiên chứa mảng
const arr = Array.isArray(data)
  ? data
  : Object.values(data).find(v => Array.isArray(v)) || [data]; // fallback nếu chỉ có 1 object

// Bắt đầu xử lý từng object
const result = arr.map(obj => {
  const newObj = { ...obj };

  // Danh sách các field cần parse (vì chúng có thể chứa chuỗi JSON)
  const jsonFields = ['unit_list', 'purchase_last_unit_price_list'];

  jsonFields.forEach(field => {
    const val = obj[field];
    // Nếu là string thì thử parse, nếu không thì giữ nguyên
    if (typeof val === 'string') {
      try {
        newObj[field] = JSON.parse(val);
      } catch (e) {
        newObj[field] = val; // giữ nguyên nếu lỗi parse
      }
    } else {
      newObj[field] = val; // giữ nguyên object
    }
  });

  return newObj;
});

// Trả kết quả cho n8n
return result.map(r => ({ json: r }));

// Chạy cho từng item
return items.map(item => {
  const rawData = item.json["Data"]; // Lấy chuỗi JSON từ trường Data
  const parsed = JSON.parse(rawData); // Parse chuỗi JSON thành object

  return {
    json: parsed // Trả về object đã parse
  };
});

// Parse JSON string
const raw = $json["Data"];
const parsed = JSON.parse(raw);

// Trả về mỗi object là một item mới
return parsed.map(item => {
  return {
    json: item
  };
});

