parameters.jsCode:
let raw = $json["jsonString"] || "";

// 1) bỏ nhãn ```json và backticks ở đầu/cuối
raw = raw
  .replace(/^\s*```?\s*json\s*/i, "")  // chỉ ở đầu chuỗi
  .replace(/\s*```?\s*$/i, "")         // chỉ ở cuối chuỗi
  .replace(/\r/g, "");

// 2) chỉ lấy phần có vẻ là JSON: từ { đầu tiên tới } cuối cùng
const start = raw.indexOf("{");
const end   = raw.lastIndexOf("}");

if (start === -1 || end === -1 || end <= start) {
  throw new Error("Chuỗi không chứa JSON đầy đủ (thiếu { hoặc }).");
}

const jsonSlice = raw.slice(start, end + 1).trim();

// 3) parse
let data;
try {
  data = JSON.parse(jsonSlice);
} catch (e) {
  // gợi ý vị trí lỗi để soi nhanh
  throw new Error(`JSON không hợp lệ. Có thể chuỗi bị cắt cụt: ${e.message}`);
}

return data;

let results = [];

for (item of items) {
    for (key of Object.keys(item.binary)) {
        results.push({
            json: {
                fileName: item.binary[key].fileName,
            sender: item.json.from.value[0].address
            },
            binary: {
                data: item.binary[key],
            }
        });
    }
}

return results;

// Lấy ngày hiện tại dạng YYYYMMDD (8 số)
const dateCode = new Date().toISOString().split('T')[0].replace(/-/g, '');

// Ghép tên file cố định
const filename = `VNTT${dateCode}-CÔNG TY TNHH THỰC PHẨM VÌ SỰ SỐNG VINA-Invoice.pdf`;

return {
  json: { filename },
  binary: {
    data: $input.item.binary.data
  }
};

let today = new Date();
const createdDate = today.toISOString().split('T')[0] + '_';
const filename = createdDate.toString() + "Telegram";

return { json: {'filename' : filename},
       binary: {
                data: $input.item.binary.data,
            }};

parameters.jsonBody:
={
  "contents": [
    {
      "parts": [
        {
          "text": "Extract key fields from the attached invoice and return JSON. Fields: seller, sellerTaxId, invoiceNo, invoiceDate, dueDate, contractNo, buyer, billingAddress, billingContact, billingEmail, billingTel, lines[].description, lines[].note, lines[].amountVND, subtotalVND, taxRatePercent, taxAmountVND, totalAmountVND."
        },
        {
          "inline_data": {
            "mime_type": "application/pdf",
            "data": "{{ $json.data }}"
          }
        }
      ]
    }
  ],
  "generationConfig": {
    "temperature": 0.1,
    "maxOutputTokens": 500,
    "topP": 0.95,
    "topK": 20,
    "responseMimeType": "application/json",
    "responseSchema": {
      "type": "object",
      "properties": {
        "seller":          { "type": "string", "nullable": true },
        "sellerTaxId":     { "type": "string", "nullable": true },
        "invoiceNo":       { "type": "string", "nullable": true },
        "invoiceDate":     { "type": "string", "nullable": true },
        "dueDate":         { "type": "string", "nullable": true },
        "contractNo":      { "type": "string", "nullable": true },
        "buyer":           { "type": "string", "nullable": true },
        "billingAddress":  { "type": "string", "nullable": true },
        "billingContact":  { "type": "string", "nullable": true },
        "billingEmail":    { "type": "string", "nullable": true },
        "billingTel":      { "type": "string", "nullable": true },
        "lines": {
          "type": "array",
          "items": {
            "type": "object",
            "properties": {
              "description": { "type": "string", "nullable": true },
              "note":        { "type": "string", "nullable": true },
              "amountVND":   { "type": "string", "nullable": true }
            }
          }
        },
        "subtotalVND":     { "type": "string", "nullable": true },
        "taxRatePercent":  { "type": "string", "nullable": true },
        "taxAmountVND":    { "type": "string", "nullable": true },
        "totalAmountVND":  { "type": "string", "nullable": true }
      }
    }
  },
  "safetySettings": [
    { "category": "HARM_CATEGORY_HARASSMENT",        "threshold": "BLOCK_NONE" },
    { "category": "HARM_CATEGORY_HATE_SPEECH",       "threshold": "BLOCK_NONE" },
    { "category": "HARM_CATEGORY_SEXUALLY_EXPLICIT", "threshold": "BLOCK_NONE" },
    { "category": "HARM_CATEGORY_DANGEROUS_CONTENT", "threshold": "BLOCK_NONE" }
  ]
}
